import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useWallet } from '../utils/WalletContext';
import axios from 'axios';

const ViewRequest = () => {
  const [request, setRequest] = useState(null);
  const [currentXfiPrice, setCurrentXfiPrice] = useState(null);
  const { id } = useParams();
  const { account, provider } = useWallet();

  useEffect(() => {
    const fetchRequestData = async () => {
      try {
        const requestResponse = await axios.get(`http://localhost:5000/api/request/${id}`);
        setRequest(requestResponse.data);

        const priceResponse = await axios.get('http://localhost:5000/api/xfi-price');
        setCurrentXfiPrice(priceResponse.data.price);
      } catch (error) {
        console.error('Error fetching request data:', error);
      }
    };

    fetchRequestData();
  }, [id]);

  const handlePayRequest = async () => {
    try {
      await axios.post('http://localhost:5000/api/pay-request', { requestId: id });
      // Refresh request data after payment
      const updatedRequest = await axios.get(`http://localhost:5000/api/request/${id}`);
      setRequest(updatedRequest.data);
    } catch (error) {
      console.error('Error paying request:', error);
    }
  };

  if (!request) return <div>Loading...</div>;

  return (
    <div>
      <h1>Payment Request Details</h1>
      <p>Amount: {request.amount} XFI</p>
      <p>Description: {request.description}</p>
      <p>Status: {request.paid ? 'Paid' : 'Pending'}</p>
      <p>XFI Price at Creation: ${request.xfiPriceAtCreation}</p>
      <p>Current XFI Price: ${currentXfiPrice}</p>
      {!request.paid && (
        <button onClick={handlePayRequest}>Pay Request</button>
      )}
    </div>
  );
};

export default ViewRequest;
