import React, { useState } from 'react';
import { useWallet } from '../utils/WalletContext';
import axios from 'axios';

const CreateRequest = () => {
  const [amount, setAmount] = useState('');
  const [description, setDescription] = useState('');
  const [expiration, setExpiration] = useState('');
  const { account } = useWallet();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!account) {
      alert('Please connect your wallet first');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/requests', {
        amount,
        description,
        expiration,
        creator: account
      });
      alert(`Request created successfully! Request ID: ${response.data.requestId}`);
      setAmount('');
      setDescription('');
      setExpiration('');
    } catch (error) {
      console.error('Error creating request:', error);
      alert('Failed to create request. Please try again.');
    }
  };

  return (
    <div className="max-w-md mx-auto">
      <h2 className="text-2xl font-bold mb-4">Create Payment Request</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label htmlFor="amount" className="block mb-1">Amount (XFI)</label>
          <input
            type="number"
            id="amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
            className="w-full p-2 border rounded"
          />
        </div>
        <div>
          <label htmlFor="description" className="block mb-1">Description</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
            className="w-full p-2 border rounded"
          ></textarea>
        </div>
        <div>
          <label htmlFor="expiration" className="block mb-1">Expiration (in hours)</label>
          <input
            type="number"
            id="expiration"
            value={expiration}
            onChange={(e) => setExpiration(e.target.value)}
            required
            className="w-full p-2 border rounded"
          />
        </div>
        <button type="submit" className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600">
          Create Request
        </button>
      </form>
    </div>
  );
};

export default CreateRequest;
