import React, { useState, useEffect } from 'react';
import { useWallet } from '../utils/WalletContext';
import axios from 'axios';

const RewardsSystem = () => {
  const [rewards, setRewards] = useState(0);
  const { account } = useWallet();

  useEffect(() => {
    if (account) {
      fetchRewards();
    }
  }, [account]);

  const fetchRewards = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/rewards/${account}`);
      setRewards(response.data.rewards);
    } catch (error) {
      console.error('Error fetching rewards:', error);
    }
  };

  const claimRewards = async () => {
    try {
      await axios.post(`http://localhost:5000/api/rewards/claim`, { account });
      fetchRewards();
    } catch (error) {
      console.error('Error claiming rewards:', error);
    }
  };

  return (
    <div className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
      <h3 className="text-xl font-bold mb-4">XFI Rewards</h3>
      <p>Available Rewards: {rewards} XFI</p>
      <button
        onClick={claimRewards}
        className="bg-green-500 text-white px-4 py-2 rounded mt-4 hover:bg-green-600"
      >
        Claim Rewards
      </button>
    </div>
  );
};

export default RewardsSystem;
