import React from 'react';
import { Link } from 'react-router-dom';
import { useWallet } from '../utils/WalletContext';

const Header = () => {
  const { account, connectWallet } = useWallet();

  return (
    <header className="bg-blue-500 text-white p-4">
      <div className="container mx-auto flex justify-between items-center">
        <h1 className="text-2xl font-bold">CrossFi Payment Request App</h1>
        <nav>
          <ul className="flex space-x-4">
            <li><Link to="/" className="hover:text-blue-200">Home</Link></li>
            <li><Link to="/create" className="hover:text-blue-200">Create Request</Link></li>
            <li><Link to="/dashboard" className="hover:text-blue-200">Dashboard</Link></li>
          </ul>
        </nav>
        {account ? (
          <span>Connected: {account.slice(0, 6)}...{account.slice(-4)}</span>
        ) : (
          <button onClick={connectWallet} className="bg-white text-blue-500 px-4 py-2 rounded">Connect Wallet</button>
        )}
      </div>
    </header>
  );
};

export default Header;
